mapper.update:
UPDATE asset_re
   SET
       re_type = ${reType}
       ,re_name              = #{reName}
       ,buy_date             = #{buyDate}
       ,buy_amt              = ${buyAmt}
       ,buy_extra_amt        = ${buyExtraAmt}
       ,loan_amt             = ${loanAmt}
       ,loan_bal_amt         = ${loanBalAmt}
       ,loan_rate            = ${loanRate}
       ,sale_date            = #{seleDate}
       ,sale_amt             = ${seleAmt}
       ,sale_income_tax_amt  = ${seleIncomeTaxAmt}
       ,sale_extra_amt       = ${seleExtraAmt}
       ,pal_amt              = ${palAmt}
       ,pal_percentage       = ${palPercentage}
       ,re_rent_id           = ${reRentId}
       ,delete_yn            = #{deleteYn} 
       ,last_update_datetime = NOW() 
WHERE asset_id = ${assetId}

mapper.select:
SELECT ar.asset_id ,ar.re_type ,cc.code_name AS re_type_name ,ar.re_name ,ar.buy_date ,ar.buy_amt ,ar.buy_extra_amt ,IFNULL(ar.loan_amt, 0) AS loan_amt ,IFNULL(ar.loan_bal_amt, 0) AS loab_bal_amt ,IFNULL(ar.loan_rate, 0.0) AS loan_rate ,IFNULL(ar.cur_expect_amt, buy_amt) AS cur_expect_amt ,IFNULL(ar.cur_cost_amt, 0) AS cur_cost_amt ,ar.sale_date ,ar.sale_amt ,ar.sale_income_tax_amt ,ar.sale_extra_amt ,ar.pal_amt ,ar.pal_percentage ,ar.re_rent_id ,ar.delete_yn ,ar.reg_datetime ,ar.last_update_datetime FROM asset_re ar LEFT OUTER JOIN common_code cc ON cc.div_code = 'RE_TYPE' AND cc.code = ar.re_type LEFT OUTER JOIN re_rent rr ON rr.re_rent_id = ar.re_rent_id WHERE 1 = 1
AND ar.asset_id = ${assetId}
AND ar.re_type = #{reType}
AND ar.delete_yn = #{deleteYn}
AND ar.member_id = ${memberId}

mapper.update:
UPDATE asset_re_rent
SET
    asset_id              = #{assetId}
    ,rent_start_date      = #{rentStartDate}
    ,rent_end_date        = #{rentEndDate}
    ,renter_name          = #{renterName}
    ,renter_contact       = #{renterContact}
    ,deposit              = ${deposit}
    ,month_fee            = ${monthFee}
    ,delete_yn            = #{deleteYn}
    ,last_update_datetime = NOW()
WHERE re_rent_id = ${reRentId}

mapper.select:
SELECT re_rent_id ,asset_id ,rent_start_date ,rent_end_date ,renter_name ,renter_contact ,deposit ,month_fee ,delete_yn ,reg_datetime ,last_update_datetime FROM asset_re_rent arr INNER JOIN asset a ON a.asset_id = arr.asset_id WHERE 1 = 1
AND arr.asset_id = ${assetId}
AND arr.member_id = ${memberId}
