mapper.update:
UPDATE asset_stock
   SET
       member_id             = ${memberId}
       ,org_cd                = #{orgCd}
       ,org_name             = #{orgName}
       ,stock_acno           = #{stockAcno}
       ,able_amt             = ${ableAmt}
       ,loan_bal_amt         = ${loanBalAmt}
       ,loan_rate            = ${loanRate}
       ,delete_yn            = #{deleteYn} 
       ,last_update_datetime = NOW() 
WHERE asset_id = ${assetId}

mapper.select:
SELECT asset_id ,member_id ,org_cd ,org_name ,stock_acno ,able_amt ,loan_bal_amt ,loan_rate ,delete_yn ,reg_datetime ,last_update_datetime FROM asset_stock stock WHERE 1 = 1
AND stock.asset_id = ${assetId}
AND stock.member_id = ${memberId}
AND stock.delete_yn = #{deleteYn}

mapper.update:
UPDATE stock_kind
SET
   asset_id          = ${assetId}
  ,member_id         = ${memberId}
  ,stock_kind_cd     = #{stockKindCd}
  ,stock_kind_name   = #{stockKindName}
  ,quantity          = ${quantity}
  ,buy_avg_price     = ${buyAvgPrice}
  ,buy_tot_price     = ${buyTotPrice}
  ,cur_unit_price    = ${curUnitPrice}
  ,cur_tot_price     = ${curTotPrice}
  ,pnl_rate          = ${pnlRate}
  ,pnl_amt           = ${pnlAmt}
  ,delete_yn         = #{deleteYn}
  ,last_update_datetime = NOW()
WHERE stock_kind_id = ${stockKindId}

UPDATE stock_kind
SET
   cur_unit_price    = ${curUnitPrice}
  ,cur_tot_price     = ${curTotPrice}
  ,pnl_rate          = ${pnlRate}
  ,pnl_amt           = ${pnlAmt}
  ,last_update_datetime = NOW()
WHERE stock_kind_id = ${stockKindId}

mapper.select:
SELECT stock_kind_id ,asset_id ,member_id ,stock_kind_cd ,stock_kind_name ,quantity ,buy_avg_price ,buy_tot_price ,cur_unit_price ,cur_tot_price ,pnl_rate ,pnl_amt ,delete_yn ,reg_datetime ,last_update_datetime FROM stock_kind sk WHERE 1 = 1
AND sk.stock_kind_id = ${stockKindId}
AND sk.member_id = ${memberId}
AND sk.delete_yn = #{deleteYn}

mapper.update:
UPDATE stock_trade
SET
         stock_trade_id       = ${stockTradeId}
        ,trade_type           = #{tradeType}
        ,stock_kind_id        = ${stockKindId}
        ,trade_datetime       = #{tradeDatetime}
        ,trade_type_name      = #{tradeTypeName}
        ,trade_quantity       = ${tradeQuantity}
        ,trade_unit_price     = ${tradeUnitPrice}
        ,trade_amt            = ${tradeAmt}
        ,bef_quantity         = ${befQuantity}
        ,aft_quantity         = ${aftQuantity}
        ,bef_buy_avg_price    = ${befBuyAvgPrice}
        ,bef_buy_tot_price    = ${befBuyTotPrice}
        ,aft_buy_avg_price    = ${aftBuyAvgPrice}
        ,aft_buy_tot_price    = ${aftBuyTotPrice}
        ,tax_amt              = ${taxAmt}
        ,fee_amt              = ${feeAmt}
        ,pnl_rate             = ${pnlRate}
        ,pnl_amt              = ${pnlAmt}
        ,delete_yn            = #{deleteYn}
        ,last_update_datetime = NOW()
WHERE stock_trade_id = ${stockTradeId}

mapper.select:
SELECT stock_trade_id ,trade_type ,cc.code_name AS trade_type_name ,stock_kind_id ,trade_datetime ,trade_type_name ,trade_quantity ,trade_unit_price ,trade_amt ,bef_quantity ,aft_quantity ,bef_buy_avg_price ,bef_buy_tot_price ,aft_buy_avg_price ,aft_buy_tot_price ,tax_amt ,fee_amt ,pnl_rate ,pnl_amt ,delete_yn ,reg_datetime ,last_update_datetime FROM stock_trade st LEFT OUTER JOIN common_code cc ON cc.div_code = 'STOCK_TRADE_TYPE' AND cc.code = st.trade_type WHERE 1 = 1
AND st.stock_trade_id = ${stockTradeId}
AND st.member_id = ${memberId}
AND st.delete_yn = #{deleteYn}
