mapper.delete:
UPDATE member_role
SET
    delete_yn = 'Y'
  ,last_update_datetime = NOW()
WHERE  member_id = ${memberId}
  AND  role_cd = #{roleCd}

mapper.select:
SELECT
r.member_id
,r.role_cd
,r.delete_yn
,r.reg_datetime
,r.last_update_datetime
FROM member_role r
    INNER JOIN member m ON m.member_id = r.member_id
WHERE 1 = 1

    AND r.member_id = ${memberId}


    AND m.member_name = #{memberName}


    AND r.delete_yn = #{deleteYn}


    AND m.email = #{email}

SELECT
r.member_id
,r.role_cd
,r.delete_yn
,r.reg_datetime
,r.last_update_datetime
FROM member_role r
INNER JOIN member m ON m.member_id = r.member_id
WHERE 1 = 1

    AND r.member_id = ${memberId}


    AND m.member_name = #{memberName}


    AND r.delete_yn = #{deleteYn}


    AND m.email = #{email}

mapper.update:
UPDATE member_token
SET
    refresh_token = #{refreshToken}
  ,refresh_token_expire_datetime = #{refreshTokenExpireDatetime}
  ,delete_yn = #{deleteYn}
  ,last_update_datetime = NOW()
WHERE  member_id = ${memberId}

mapper.delete:
UPDATE member_token
SET
   delete_yn = #{deleteYn}
  ,last_update_datetime = NOW()
WHERE  member_id = ${memberId}

mapper.select:
SELECT
t.member_id
,t.refresh_token
,t.refresh_token_expire_datetime
,TIMESTAMPDIFF(SECOND, NOW(), t.refresh_token_expire_datetime) AS remaining_refresh_token_seconds
,t.delete_yn
,t.reg_datetime
,t.last_update_datetime
FROM member_token t
INNER JOIN member m ON m.member_id = t.member_id
WHERE 1 = 1

    AND m.member_id = ${memberId}


    AND m.member_name = #{memberName}


    AND t.refresh_token = #{refreshToken}


    AND t.delete_yn = #{deleteYn}


    AND m.email = #{email}
